CREATE TABLE Users (  id INT PRIMARY KEY AUTO_INCREMENT,  userAccount VARCHAR(16) NOT NULL UNIQUE COMMENT '用户账号',  userName VARCHAR(30) NOT NULL COMMENT '用户昵称',  password VARCHAR(64) NOT NULL COMMENT '加密后的密码',  email VARCHAR(50) UNIQUE COMMENT '邮箱',  phone VARCHAR(20) UNIQUE COMMENT '手机号',  avatar VARCHAR(255) DEFAULT NULL COMMENT '头像URL',  isActive BOOLEAN DEFAULT TRUE COMMENT '账户是否激活',  createdAt DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,  updatedAt DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  INDEX idx_user_account(userAccount),  INDEX idx_email(email));